// Process user preferences
const items = $input.all();

// Process each item to extract and normalize preferences
const processedItems = items.map(item => {
  const userPrefs = item.json;

  // If user has preferences, process them
  if (userPrefs && Array.isArray(userPrefs)) {
    const prefsMap = {};
    userPrefs.forEach(pref => {
      if (pref.preference_key && pref.preference_value) {
        prefsMap[pref.preference_key] = pref.preference_value;
      }
    });

    return {
      ...item.json,
      user_preferences: prefsMap
    };
  }

  // If no preferences, provide defaults
  return {
    ...item.json,
    user_preferences: {}
  };
});

return processedItems;

// Task categorization code
const tasks = $input.all().map(i => i.json);
const now = new Date();

// Categorize tasks
const categorizedTasks = {
  overdue: tasks.filter(t => t.due_date && new Date(t.due_date) < now && t.status !== 'completed'),
  today: tasks.filter(t => t.due_date && new Date(t.due_date).toDateString() === now.toDateString()),
  upcoming: tasks.filter(t => t.due_date && new Date(t.due_date) > now && t.status !== 'completed')
};

// Calculate statistics
const stats = {
  total: tasks.length,
  completed: tasks.filter(t => t.status === 'completed').length,
  overdue: categorizedTasks.overdue.length,
  today: categorizedTasks.today.length
};

// Prepare output
return { json: { tasks: categorizedTasks, stats } };

// Filter content based on user preferences
const items = $input.all();

const filteredItems = items.map(item => {
  const userData = item.json;
  const prefs = userData.user_preferences || {};

  // Create filtered task data based on preferences
  const filteredTasks = {
    overdue: userData.tasks.overdue || [],
    today: userData.tasks.today || [],
    upcoming: userData.tasks.upcoming || []
  };

  // Apply content filtering based on preferences
  if (prefs.content_sections) {
    // Filter by specific content sections if user has preferences
    const sections = prefs.content_sections.split(',').map(s => s.trim());

    if (!sections.includes('overdue')) {
      filteredTasks.overdue = [];
    }
    if (!sections.includes('today')) {
      filteredTasks.today = [];
    }
    if (!sections.includes('upcoming')) {
      filteredTasks.upcoming = [];
    }
  }

  // Adjust briefing length based on preference
  let maxTasks = 5; // Default
  if (prefs.briefing_style === 'concise') {
    maxTasks = 3;
  } else if (prefs.briefing_style === 'detailed') {
    maxTasks = 10;
  }

  // Limit task counts if needed
  filteredTasks.overdue = filteredTasks.overdue.slice(0, maxTasks);
  filteredTasks.today = filteredTasks.today.slice(0, maxTasks);
  filteredTasks.upcoming = filteredTasks.upcoming.slice(0, maxTasks);

  // Adjust tone based on preference
  const adjustedStats = {
    ...userData.stats,
    maxTasks: maxTasks
  };

  return {
    ...userData,
    tasks: filteredTasks,
    stats: adjustedStats
  };
});

return filteredItems;

