const tasks = $input.all().map(i => i.json);
const now = new Date();
const todayStart = new Date(now.getFullYear(), now.getMonth(), now.getDate());
const todayEnd = new Date(now.getFullYear(), now.getMonth(), now.getDate() + 1);

// Helpers
const getEndOfWeek = (d) => {
  const date = new Date(d);
  const day = date.getDay(); 
  const diff = date.getDate() - day + (day === 0 ? -6 : 1) + 6; 
  return new Date(date.setDate(diff));
};
const endOfWeek = getEndOfWeek(todayStart);
const endOfMonth = new Date(now.getFullYear(), now.getMonth() + 1, 0);

// Buckets
const overdue = [];
const today = [];
const thisWeek = [];
const thisMonth = [];
const backlog = []; // No Due Date tasks

// Status Counters
let inProgressCount = 0;
let pendingCount = 0;

tasks.forEach(t => {
  // Count Status
  if (t.status === 'in_progress') inProgressCount++;
  if (t.status === 'pending') pendingCount++;

  // Create Tag
  const statusIcon = t.status === 'in_progress' ? '🏃' : '⏳';
  const statusTag = `[${statusIcon} ${t.status.toUpperCase().replace('_', ' ')}]`;

  // Combine Title + Status
  const taskString = `${t.title} ${statusTag}`;

  if (!t.due_date) {
     backlog.push(taskString);
     return;
  }

  const due = new Date(t.due_date);

  if (due < todayStart) {
    overdue.push(`${taskString} (Due: ${t.due_date.split('T')[0]})`);
  } else if (due >= todayStart && due < todayEnd) {
    today.push(`${taskString} [${t.priority.toUpperCase()}]`);
  } else if (due >= todayEnd && due <= endOfWeek) {
    thisWeek.push(`${taskString} (${t.due_date.split('T')[0]})`);
  } else if (due > endOfWeek && due <= endOfMonth) {
    thisMonth.push(`${taskString} (${t.due_date.split('T')[0]})`);
  }
});

return {
  json: {
    date: now.toDateString(),
    stats: {
      overdue_count: overdue.length,
      today_count: today.length,
      total_pending: tasks.length,
      status_summary: {
          in_progress: inProgressCount,
          pending: pendingCount
      }
    },
    lists: {
      overdue,
      today,
      this_week: thisWeek,
      this_month: thisMonth,
      backlog
    }
  }
};

